    import React, { useState } from'react';
import axios from 'axios';

const BASE_URL = 'https://api.coingecko.com/api/v3/simple/price';

const TradeAdviser =()=>{

    const [crypto, setCrypto] = useState("");
    const [fiat, setFiat] = useState("");
    const [cryptoPrice, setCryptoPrice] = useState(0);
    const [fiatPrice, setFiatPrice] = useState(0);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [conversionRate, setConversionRate] = useState(1);
    const [convertedPrice, setConvertedPrice] = useState(0);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            setLoading(true);
            setError(null);
            const response = await axios.get(`${BASE_URL}?ids=${crypto}&vs_currencies=${fiat}`);
            setCryptoPrice(response.data[crypto].usd);
            setFiatPrice(response.data[crypto].quote[fiat].price);
            setConversionRate(response.data[crypto].quote[fiat].price / response.data[crypto].usd);
            setConvertedPrice(conversionRate * fiatPrice);
            setLoading(false);
        } catch (error) {
            setError(error.message);
            setLoading(false);
        }
    }

    return(
        <>
           <h1> Welcome to TradeAdviser</h1>
            {loading ? <p>Loading...</p> : error ? <p>Error: {error}</p> : (
                <form onSubmit={handleSubmit}>
                    <label>
                        Crypto:
                        <input type="text" value={crypto} onChange={(e) => setCrypto(e.target.value)} />
                    </label>
                    <br />
                    <label>
                        Fiat:
                        <input type="text" value={fiat} onChange={(e) => setFiat(e.target.value)} />
                    </label>
                    <br />
                    <button type="submit">Get Price</button>
                </form>


            )}



           </>)
}
export default TradeAdviser;
